import React , { useEffect } from "react";
import "./Person.css"
import { useState } from "react";

import Axios from 'axios'
const Person = () =>
{
    const[Pname,setPName] = useState("");
    const[Lname,setLName] = useState("");

    const[Sex,setSex] = useState("");
    
    const[Bdate,setBDate] = useState(0);
    const[personList,setPersonList] = useState([]);

    const addPerson = () =>
    {

        Axios.post('http://localhost:3001/persons/insert',{Pname:Pname,Lname:Lname,Sex:Sex,Bdate:Bdate})
        .then(()=>{
            //hemen eklemiyor 
            setPersonList(
                [...personList,
                {
                    Pname:Pname,
                    Lname:Lname,
                    Sex:Sex,
                    Bdate:Bdate,
                },
            ]);
        });
    }
    
    const getPersons =() =>{

        Axios.get('http://localhost:3001/persons/get').then((response) => {
            setPersonList(response.data);
            //console.log(response.data);
            
        });
    };
    useEffect(() => {
        getPersons();
     }, [])
/*
    useEffect(() => {
       getProducts();
    }, [])

    const deletePerson = (id) =>{
        Axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
            setProductList(
                productList.filter((val) =>{
                    return val.id != id;
                })
            );
        });
        console.log(id);
    };
    */
  
    return (

        <div>
        
        <form>
            
            <div class="input-container">
                <input type="text" onChange={(event) => {setPName(event.target.value)}} required=""/>
                <label>First Name </label>		
            </div>
            <div class="input-container">
                <input type="text" onChange={(event) => {setLName(event.target.value)}} required=""/>
                <label>Last Name</label>		
            </div>
            <div class="input-container">
                <input type="text" onChange={(event) => {setSex(event.target.value)}} required=""/>
                <label>Sex</label>		
            </div>
            <div class="input-container">		
                <input type="text" onChange={(event) => {setBDate(event.target.value)}} required=""/>
                <label>Birth Date</label>
            </div>
                <button type="button" onClick={addPerson}  class="btn">Add Person</button>
        </form>	


    

    <div class="container" >
            
            {personList.map((val,key) =>{
                return (
                    <div key={key} class="movie-card" >
                        
                        <div >
                           

                            <div class="movie-content">
                                <div class="movie-content-header">
                                    
                                    <h3 class="movie-title">{val.Pname} {val.Lname}</h3>
                                   
                                </div>
                                <div class="movie-info">
                                    <div class="info-section">
                                        <label>Sex</label>
                                        <span>{val.Sex}</span>
                                    </div>
                                    <div class="info-section">
                                        <label>Birth Date</label>
                                        <span>{val.Bdate}</span>
                                    </div>
                            
                                </div>
                            </div>
                            <button class="btn">Update</button>
                            <button class="btn">Delete</button>
                        </div>
                     </div>
                    
                    );
        
                })
            }
            
        </div>
      
   
        </div>
    
    )

}

export default Person
