import React from "react";
import "./product.css"
import { useState } from "react";
import Axios from 'axios'
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

const Product = () =>
{
    const[name,setName] = useState("");

    const[releaseDate,setReleaseDate] = useState("");
    
    const[rating,setRating] = useState(0);
    
    const[trailerUrl,setTrailerUrl] = useState("");
    
    const[imageUrl,setImageUrl] = useState("");

    const[productList,setProductList] = useState([]);

    const addProduct = () =>
    {

        Axios.post('http://localhost:3001/products',{name:name,releaseDate:releaseDate,rating:rating,trailerUrl:trailerUrl,imageUrl:imageUrl})
        .then(()=>{
            
            console.log(name+ " "+ releaseDate+ " "+ rating + " " + trailerUrl +" " +imageUrl +" success");
        });
    }
   
    // Axios.get('http://localhost:3001/products/allproducts') kabul etmiyor!
    const getProducts =() =>{

        Axios.get('http://localhost:3001/allproducts').then((response) => {
            setProductList(response.data);
            console.log(response.data);
        });
    };

    return (
    <div class="box">
        <form>
            
            <div class="input-container">
                <input type="text" onChange={(event) => {setName(event.target.value)}} required=""/>
                <label>Name</label>		
            </div>
            <div class="input-container">
                <input type="text" onChange={(event) => {setReleaseDate(event.target.value)}} required=""/>
                <label>Release Date</label>		
            </div>
            <div class="input-container">
                <input type="text" onChange={(event) => {setRating(event.target.value)}} required=""/>
                <label>Rating</label>		
            </div>
            <div class="input-container">		
                <input type="text" onChange={(event) => {setTrailerUrl(event.target.value)}} required=""/>
                <label>Trailer URL</label>
            </div>
            <div class="input-container">		
                <input type="text" onChange={(event) => {setImageUrl(event.target.value)}} required=""/>
                <label>Image URL </label>
            </div>
                <button type="button" onClick={addProduct} class="btn">Add Product</button>
                
                
        </form>	
        <div>

            <button type="button" onClick={getProducts} class="btn">Get Products</button>
        
            {productList.map((val,key) =>{
                
                    return (
                        <div key={key}>
                                 <div className = "card">
                                    <div className="container"> 
                                        <h3 > Name: {val.name} </h3>
                                        <h3 > Rating : {val.rating}</h3>
                                        <h3 > Release Date: {val.releaseDate}</h3>
                                        <h3 > Trailer Url: {val.trailerUrl}</h3>
                                        <h3 > Image Url: {val.imageUrl}</h3>
                                    </div>
                            </div>
                        </div> 
                    );
                })
    
            }

        </div> 
       
    </div>

    )

}/*
<Card style={{ width: '18rem' }}>
                        <Card.Img variant="top" src={val.imageUrl} />
                            <Card.Body>
                                <Card.Title><span key={key}>{val.name}</span></Card.Title>
                                    <Card.Text>
                                    <span key={key}> {val.releaseDate}</span>
                                    <span key={key}>  {val.rating}</span>      
                                           
                                    </Card.Text>
                                    <Button variant="primary">Go To Trailer</Button>
                            </Card.Body>
                      
                       
                    </Card>
                    */

export default Product
