import React, { useEffect } from "react";
import "./product.css"
import { useState } from "react";
import Axios from 'axios'
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

const Product = () =>
{
    const[name,setName] = useState("");

    const[releaseDate,setReleaseDate] = useState("");
    
    const[rating,setRating] = useState(0);
    
   
    
    const[imageUrl,setImageUrl] = useState("");

    const[productList,setProductList] = useState([]);



    const addProduct = () =>
    {

        Axios.post('http://localhost:3001/products',{name:name,releaseDate:releaseDate,rating:rating,imageUrl:imageUrl})
        .then(()=>{
            
            console.log(name+ " "+ releaseDate+ " "+ rating + " "  +" " +imageUrl +" success");
        });
    }
   
    // Axios.get('http://localhost:3001/products/allproducts') kabul etmiyor!

    /// NOTE val. Dbdeki column nameler olmalı yani {val.Name} gibi
    const getProducts =() =>{

        Axios.get('http://localhost:3001/allproducts').then((response) => {
            setProductList(response.data);
            //console.log(response.data);
            
        });
    };

    useEffect(() => {
       getProducts();
    }, [])


    return (
       
        
        
        <div class="container">
            <button type="button" onClick={addProduct} class="btn">Add Product</button>
            {productList.map((val,key) =>{
                return (
                    <div key={key} class="movie-card">
                        
                        <div >
                            <div class="movie-header manOfSteel">
                                <div class="header-icon-container">
                                
                                    <img src={val.Image_url} class="material-icons header-icon"/>
                                    
                                </div>
                            </div>
                            <div class="movie-content">
                                <div class="movie-content-header">
                                    
                                    <h3 class="movie-title">{val.Name}</h3>
                                    
                                </div>
                                <div class="movie-info">
                                    <div class="info-section">
                                        <label>Release Date</label>
                                        <span>{val.Release_date}</span>
                                    </div>
                                    <div class="info-section">
                                        <label>Rating</label>
                                        <span>{val.Rating}</span>
                                    </div>
                            
                                </div>
                            </div>
                        </div>
                     </div>
                    
                    );
        
                })
            }
            
        </div>

       /*  <div class="cards">
            {productList.map((val,key) =>{
                return (
                    <div key={key} >
                        <div class="card">
                            <img src={val.Image_url} class="imageclass" />
                                    <div class="container" >
                                        <h4><b>Name: {val.Name}</b></h4>
                                        <p>Rating: {val.Rating}</p>
                                        <p>Release Date: {val.Release_date}</p>
                                            
                                    </div>
                            </div>
                                
                        </div> 
                    );
        
                })
            }

        </div> */

    )

}/*
<Card style={{ width: '18rem' }}>
                        <Card.Img variant="top" src={val.imageUrl} />
                            <Card.Body>
                                <Card.Title><span key={key}>{val.name}</span></Card.Title>
                                    <Card.Text>
                                    <span key={key}> {val.releaseDate}</span>
                                    <span key={key}>  {val.rating}</span>      
                                           
                                    </Card.Text>
                                    <Button variant="primary">Go To Trailer</Button>
                            </Card.Body>
                      
                       
                    </Card>
                    */

export default Product
