    const[sex,setSex] = useState("");
    
    const[bdate,setBDate] = useState(0);
    
    
  
    return (
       <div className="Actor">
        <div className="information">
            <label>First Name:</label>
            <input type="text" onChange={(event) => {setPName(event.target.value)}}></input>

            <label>Last Name:</label>
            <input type="text" onChange={(event) => {setLName(event.target.value)}}></input>

            <label>Sex:</label>
            <input type="text" onChange={(event) => {setSex(event.target.value)}}></input>
            

