import React from "react";
import "./Person.css"
import { useState } from "react";

const Person = () =>
{
    const[pname,setPName] = useState("");
    const[lname,setLName] = useState("");

    const[sex,setSex] = useState("");
    
    const[bdate,setBDate] = useState(0);
    
    const getProducts =() =>{

        Axios.get('http://localhost:3001/allpersons').then((response) => {
            setProductList(response.data);
            //console.log(response.data);
            
        });
    };

    useEffect(() => {
       getProducts();
    }, [])

    const deleteProduct = (id) =>{
        Axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
            setProductList(
                productList.filter((val) =>{
                    return val.id != id;
                })
            );
        });
        console.log(id);
    };
  
    return (
        <div class="box">
        <form>
            
            <div class="input-container">
                <input type="text" onChange={(event) => {setPName(event.target.value)}} required=""/>
                <label>First Name </label>		
            </div>
            <div class="input-container">
                <input type="text" onChange={(event) => {setLName(event.target.value)}} required=""/>
                <label>Last Name</label>		
            </div>
            <div class="input-container">
                <input type="text" onChange={(event) => {setSex(event.target.value)}} required=""/>
                <label>Sex</label>		
            </div>
            <div class="input-container">		
                <input type="text" onChange={(event) => {setBDate(event.target.value)}} required=""/>
                <label>Birth Date</label>
            </div>
                <button type="button"  class="btn">Add Person</button>
        </form>	


        <div class="container" >
            
            {personList.map((val,key) =>{
                return (
                    <div key={key} class="movie-card" >
                        
                        <div >
                            <div class="movie-header manOfSteel">
                                <div class="header-icon-container">
                                
                                    <img src={val.Image_url} class="material-icons header-icon"/>
                                    
                                </div>
                            </div>
                            <div class="movie-content">
                                <div class="movie-content-header">
                                    
                                    <h3 class="movie-title">{val.Name}</h3>
                                    
                                </div>
                                <div class="movie-info">
                                    <div class="info-section">
                                        <label>Release Date</label>
                                        <span>{val.Release_date}</span>
                                    </div>
                                    <div class="info-section">
                                        <label>Rating</label>
                                        <span>{val.Rating}</span>
                                    </div>
                            
                                </div>
                            </div>
                            <button class="btn" onClick={deletePerson(val.Id)}>Delete</button>
                            
                            <button class="btn">Update</button>
                        </div>
                     </div>
                    
                    );
        
                })
            }
            
        </div>
    </div>

    )

}

export default Person
