import React , { useEffect } from "react";
import "./MoviePage.css"
import { useState } from "react";
//import {getProductId} from "../Product/product"
import Axios from 'axios'

const MoviePage = () =>{
    //console.log(window.location.pathname)
      var url = window.location.pathname
    var Movie_ID = url.substring(url.length-1,url.length)
    console.log(Movie_ID) 

    const[U_Id,setU_Id] = useState("");
    const[ID,setID] = useState("");
    const[P_Id,setP_Id] = useState("");
    const[Text,setText] = useState("");
    const[Date,setDate] = useState(""); 

    const[reviewList,setReviewList] = useState([]);
    const addReviewCurrentMovie = () =>
    {
        
        Axios.post("http://localhost:3001/productReview",{Text:Text,Movie_ID:ID})
        .then(()=>{
            //hemen eklemiyor 
            setReviewList(
                [...reviewList,
                {
                   
                    Text:Text,
                    Movie_ID:ID
                   
                },
            ]);
        });
        console.log("Text: " + Text)
        console.log("Movie_Id: " + ID)
    }
   




    /* const getReviews = () =>
    {

        Axios.get(`http://localhost:3000/products/${User_Id}`).then((response) => {
            setReviewList(response.data);
            //console.log(response.data);
            
        });
    }
    */
    useEffect(() => {
        setID(Movie_ID)
        getReviews();
     }, []) 
     
    return(
            <div>
                <form>
                    <div className="input-container">
                        <input type="text" 
                            onChange={(event) => {setText(event.target.value)}}
                         required=""/>
                        <label>Text</label>
                    </div>

                    <button type="button" onClick={addReviewCurrentMovie} className="btn">Add Review</button>
                </form>


            <div class="container" >
            
            {reviewList.map((val,key) =>{
                return (
                    <div key={key} class="movie-card" >
                        
                        <div >
                           

                            <div class="movie-content">
                                <div class="movie-content-header">
                                    
                                    <h3 class="movie-title">{val.Pname} {val.Lname}</h3>
                                   
                                </div>
                                <div class="movie-info">
                                    <div class="info-section">
                                        <label>Sex</label>
                                        <span>{val.Sex}</span>
                                    </div>
                                    <div class="info-section">
                                        <label>Birth Date</label>
                                        <span>{val.Bdate}</span>
                                    </div>
                            
                                </div>
                            </div>
                            
                        </div>
                     </div>
                    
                    );
        
                })
            }
            
        </div>
            </div>


    )
}
export default MoviePage
