import React from "react";
import "./movie.css"
import { useState } from "react";

const Movie = () =>
{
    const[name,setName] = useState("");

    const[releaseDate,setReleaseDate] = useState("");
    
    const[Rating,setRating] = useState(0);
    
    const[trailerUrl,setTrailerUrl] = useState("");
    
    return (
       <div className="Movie">
        <div className="information">
            <label>Name:</label>
            <input type="text" onChange={(event) => {setName(event.target.value)}}></input>
            <label>Release Date:</label>
            <input type="text" onChange={(event) => {setReleaseDate(event.target.value)}}></input>
            <label>Rating:</label>
            <input type="text" onChange={(event) => {setRating(event.target.value)}}></input>
            <label>Trailer url:</label>
            <input type="text" onChange={(event) => {setTrailerUrl(event.target.value)}}></input>
            <button>Add Movie</button>
        </div>
       </div>

    )

}

export default Movie
