import React , { useEffect } from "react";
import "./MoviePage.css"
import { useState } from "react";
//import {getProductId} from "../Product/product"
import Axios from 'axios'

const MoviePage = () =>{
    //console.log(window.location.pathname)
    var url = window.location.pathname
    var U_Id = url.substring(url.length-1,url.length)
    console.log(U_Id)


    const[reviewList,setReviewList] = useState([]);


    const getReviews =() =>{

        Axios.get('http://localhost:3001/allproducts').then((response) => {
            setReviewList(response.data);
            //console.log(response.data);
            
        });
    };

    useEffect(() => {
        getReviews();
    }, [])
   /* const getReviews = () =>
    {

        Axios.post('http://localhost:3001/movieReviews',{U_Id:U_Id}).then((response) => {
            
                console.log(response.data);
   
        });

    }
    useEffect(() => {
        getReviews();
     }, [])
     */
    return(
            <div>
                <form>

                    <div className="input-container">
                        <input type="text" 
                           
                         required=""/>
                        <label>Rating </label>
                    </div>
                    <div className="input-container">
                        <input type="text" 
                           
                         required=""/>
                        <label>Text</label>
                    </div>

                    <button type="button" className="btn">Addhbfdghfdhdgfd Review</button>
                </form>


            <div class="container" >
            
            {reviewList.map((val,key) =>{
                return (
                    <div key={key} class="movie-card" >
                        
                        <div >
                           

                            <div class="movie-content">
                                <div class="movie-content-header">
                                    
                                    <h3 class="movie-title">{val.Pname} {val.Lname}</h3>
                                   
                                </div>
                                <div class="movie-info">
                                    <div class="info-section">
                                        <label>Sex</label>
                                        <span>{val.Sex}</span>
                                    </div>
                                    <div class="info-section">
                                        <label>Birth Date</label>
                                        <span>{val.Bdate}</span>
                                    </div>
                            
                                </div>
                            </div>
                            
                        </div>
                     </div>
                    
                    );
        
                })
            }
            
        </div>
            </div>


    )
}
export default MoviePage
